import React, { Component } from "react";
import { toast } from "react-toastify";
class NetworkNavigator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      networkStatus: null,
    };
    this.networkStatus = this.props.status;
  }

  componentDidUpdate() {
    if (this.networkStatus === false) {
      toast("Offline");
    }
  }

  render() {
    return <div></div>;
  }
}

export default NetworkNavigator;
