import React, { Component } from "react";
import { toast } from "react-toastify";
class NetworkNavigator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      networkStatus: null,
    };
    this.updateNetworkStatus = this.updateNetworkStatus.bind(this);
  }

  componentDidMount() {
    this.setState({ networkStatus: window.navigator.onLine });
  }

  updateNetworkStatus() {
    if (this.state.networkStatus !== true) {
      this.setState({ networkStatus: window.navigator.onLine }, () => {
        toast("Offline");
      });
    }
  }

  render() {
    return <div onChange={this.updateNetworkStatus}></div>;
  }
}

export default NetworkNavigator;
