import React, { Component } from "react";
import { toast } from "react-toastify";
class NetworkNavigator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      networkStatus: window.navigator.onLine,
    };
    this.showNetworkError = this.showNetworkError.bind(this);
  }

  showNetworkError() {
    console.log(this.state.networkStatus);
    if (this.state.networkStatus !== true) {
      toast("O ne, atrodo praradote interneto ryšį\n");
    }
  }

  componentDidUpdate() {}

  render() {
    return <div onChange={this.showNetworkError}></div>;
  }
}

export default NetworkNavigator;
