import React, { Component } from "react";

class MaincComponent extends Component {
  constructor(props) {
    super(props);
    this.fetchLink = "https://api.artefaktas.eu/";
    this.readMoreText = `Skaityti daugiau`;
    this.homeText = `Sugrįžti į pagrindinį`;
    this.caracLimit = 100;
    this.state = {
      data: null,
      selectedPost: null,
    };
    this.cardStyle = {
      width: 100 + "%",
    };
    this.setPostState = this.setPostState.bind(this);
  }

  componentDidMount() {
    async function getData(passedParam) {
      const res = await fetch(passedParam);
      const jsonData = await res.json();
      return jsonData;
    }
    getData(this.fetchLink).then((dat) => {
      this.setState({ data: dat });
    });
  }

  showPost() {
    if (this.state.selectedPost !== null) {
      return (
        <div className="col-lg-10 col-md-10 col-sm-12 col-xs-12 mx-auto align-items-center">
          <div className="card my-4" style={this.cardStyle}>
            <div className="card-body">
              <h4 className="card-title">{this.state.selectedPost.title.$t}</h4>
              <p
                className="card-text"
                dangerouslySetInnerHTML={{
                  __html: post.content.$t,
                }}
              ></p>
              <a
                data-post={post.id.$t}
                className="btn btn-lg btn-dark postButton"
                onClick={() => {
                  this.setState({ selectedPost: post });
                }}
              >
                {this.readMoreText}
              </a>
            </div>
          </div>
        </div>
      );
    }
  }

  setPostState() {}

  render() {
    return (
      <div>
        {this.state.data !== null && this.state.data !== ""
          ? this.state.data.map((post, index) => {
              return (
                <div
                  className="col-lg-10 col-md-10 col-sm-12 col-xs-12 mx-auto align-items-center"
                  key={index}
                >
                  <div className="card my-4" style={this.cardStyle}>
                    <div className="card-body">
                      <h4 className="card-title">{post.title.$t}</h4>
                      <p
                        className="card-text"
                        dangerouslySetInnerHTML={{
                          __html: post.content.$t.slice(0, this.caracLimit),
                        }}
                      ></p>
                      <a
                        data-post={post.id.$t}
                        className="btn btn-lg btn-dark postButton"
                        onClick={() => {
                          this.setState({ selectedPost: post }, () => {
                            console.log(this.state.selectedPost);
                            this.showPost();
                          });
                        }}
                      >
                        {this.readMoreText}
                      </a>
                    </div>
                  </div>
                </div>
              );
            })
          : "Loading..."}
      </div>
    );
  }
}

export default MaincComponent;
