import React, { Component } from "react";
import { ToastContainer, toast } from "react-toastify";
class NetworkNavigator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      networkStatus: null,
    };
    this.networkStatus = this.props.status;
    this.statusCheck = this.statusCheck.bind(this);
  }

  statusCheck() {
    if (this.networkStatus === false) {
      window.alert(this.networkStatus);
      toast("Offline");
    } else {
      window.alert(this.networkStatus);
      toast("Online");
    }
  }

  componentDidUpdate() {
    this.statusCheck();
  }

  render() {
    return (
      <div>
        <ToastContainer />
      </div>
    );
  }
}

export default NetworkNavigator;
