import React, { Component } from "react";
import { toast } from "react-toastify";
class NetworkNavigator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      networkStatus: null,
    };
  }

  componentDidUpdate() {
    this.setState({ networkStatus: window.navigator.onLine }, () => {
      if (this.state.networkStatus === false) {
        toast("Heyo");
      }
    });
  }

  render() {
    return <div onChange={this.showNetworkError}></div>;
  }
}

export default NetworkNavigator;
